<script>
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import $ from 'jquery'
import json from "./CafeInfo.json";
import {antPath} from 'leaflet-ant-path';
export default {
  name: "gray-page2",

    data:function(){
      return {
          map:null,
          locations: json,
      }
    },
  created: function () {
    document.body.style.backgroundColor = "#E2E2E2";
  },
  destroyed: function () {
    document.body.style.backgroundColor = null;
  },
    methods: {
      CalcDistanceBetween(lat1, lon1, lat2, lon2) {
        //Radius of the earth in:  1.609344 miles,  6371 km  | var R = (6371 / 1.609344);
         var R = 3958.7558657440545; // Radius of earth in Miles
         var dLat = (lat2-lat1) * Math.PI / 180;;
         var dLon = (lon2-lon1) * Math.PI / 180;;
         var a = Math.sin(dLat/2) * Math.sin(dLat/2) +
         Math.cos((lat1) * Math.PI / 180) * Math.cos((lat2) * Math.PI / 180) *
         Math.sin(dLon/2) * Math.sin(dLon/2);
         var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
         var d =Math.round((R * c *1609.344)*100/100) ;

         return d;
   },
        drawpolyline(lat,lon){
            var pointA = new L.LatLng(41.9972, 21.4331);
            var pointB = new L.LatLng(lat, lon);
            var pointList = [pointA, pointB];

            var polyline=L.polyline.antPath(pointList,{
                color: 'red',
                opacity: 0.8,
                dashArray: '20, 20', dashOffset: '0',
                smoothFactor: 1
            }).addTo(this.map);
            this.map.fitBounds(polyline.getBounds());
             L.marker([lat,lon]).addTo(this.map);

            $("#searchbox").on('keypress',function(e) {
                if(e.which == 13) {
                    this.map.remove(polyline);
                }
            });
        },
        searchlocations(){
            var searchstring = $('#searchbox').val();
            var meters=0;
            let placeFound = this.locations.find(m => m.Name.includes(searchstring));
            if(placeFound){
                meters=this.CalcDistanceBetween(41.9972, 21.4331,placeFound.Latitude, placeFound.Longitude);
                $("#results").empty().append("You are "+meters+"m \n" +
                    "away from your\n" +
                    "desired destination");
                document.getElementById("searchbox").value = "";
                document.getElementById("results").value = "";
                this.drawpolyline(placeFound.Latitude,placeFound.Longitude);

            }else{
                $("#results").empty().append("not in the database");
                document.getElementById("searchbox").value = "";


            }

        }
    },

    mounted() {
    document.body.style.backgroundColor = "#E2E2E2";

        this.map=L.map('osmmap',{
            center: [41.9972, 21.4331],
            minZoom: 2,
            zoom: 16
        })
        L.tileLayer('http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors',
            subdomains: ['a','b','c']
        }).addTo(this.map);
        this.map.dragging.enable();

}
};



</script>
